using System;\nusing System.Collections.Generic;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        int numElements = 10_000_000;\n        var hashSet = new HashSet<int>();\n        var array = new int[numElements];\n\n        // Populate the hash set and array\n        for (int i = 0; i < numElements; i++)\n        {\n            hashSet.Add(i);\n            array[i] = i;\n        }\n\n        // Warm up the hash set and array\n        for (int i = 0; i < 1000; i++)\n        {\n            hashSet.Contains(i);\n            Array.BinarySearch(array, i);\n        }\n\n        // Measure the lookup performance\n        var sw = Stopwatch.StartNew();\n        for (int i = 0; i < 1000; i++)\n        {\n            hashSet.Contains(i);\n        }\n        sw.Stop();\n        Console.WriteLine($"Hash lookup: {sw.ElapsedMilliseconds} ms");\n\n        sw.Restart();\n        for (int i = 0; i < 1000; i++)\n        {\n            Array.BinarySearch(array, i);\n        }\n        sw.Stop();\n        Console.WriteLine($"Binary search: {sw.ElapsedMilliseconds} ms");\n    }\n}