using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\npublic class BandwidthCalculator\n{\n    private readonly HttpClient _httpClient;\n\n    public BandwidthCalculator()\n    {\n        _httpClient = new HttpClient();\n    }\n\n    public async Task CalculateBandwidthAsync()\n    {\n        var startTime = DateTime.UtcNow;\n        var response = await _httpClient.GetAsync("http://your-asp-net-page.com");\n        var endTime = DateTime.UtcNow;\n\n        var totalTimeTaken = (endTime - startTime).TotalSeconds;\n        var totalBytesTransferred = response.Content.Headers.ContentLength ?? 0;\n\n        var bandwidth = totalBytesTransferred / totalTimeTaken;\n\n        Console.WriteLine($"Bandwidth: {bandwidth} bytes/second");\n    }\n}