var cfg = new Configuration();\ncfg.DataBaseIntegration(db => {\n    db.ConnectionFactory = () => new OracleConnection("Data Source=YourOracleDB;User ID=YourUserID;Password=YourPassword;");\n    db.Dialect<Oracle10gDialect>();\n    db.SchemaUpdate(schema => {\n        schema.Action = SchemaAutoAction.Update;\n        schema RetrieveAllSchemaInfo = false; // Set this to false to avoid the exception\n    });\n});