routes.MapRoute(\n    name: "GetBookmarks",\n    template: "users/{username}/bookmarks",\n    defaults: new { controller = "Bookmarks", action = "GetBookmarks" }\n);\n\nroutes.MapRoute(\n    name: "UpdateBookmark",\n    template: "users/{username}/bookmarks/{bookmarkid}",\n    defaults: new { controller = "Bookmarks", action = "UpdateBookmark" },\n    constraints: new { httpMethod = new HttpMethodConstraint("PUT") }\n);