using System.Web;\nusing System.Web.Caching;\n\npublic class UserInfoCache\n{\n    private const string CACHE_KEY = "UserInfo";\n\n    public static void SetUserInfo(string username, int userID, string email)\n    {\n        var cache = HttpRuntime.Cache;\n        var userInfo = new UserInfo { Username = username, UserID = userID, Email = email };\n        cache.Insert(CACHE_KEY + username, userInfo);\n    }\n\n    public static UserInfo GetUserInfo(string username)\n    {\n        var cache = HttpRuntime.Cache;\n        return cache[CACHE_KEY + username] as UserInfo;\n    }\n}\n\npublic class UserInfo\n{\n    public string Username { get; set; }\n    public int UserID { get; set; }\n    public string Email { get; set; }\n}