using System;\nusing System.Text.RegularExpressions;\n\npublic class SomeAttribute : Attribute\n{\n    private static readonly Regex GuidPattern = new Regex(@"^[0-9a-fA-F]{8}-([0-9a-fA-F]{4}-){3}[0-9a-fA-F]{12}$");\n\n    private string foreignIdentificator;\n\n    public string ForeignIdentificator\n    {\n        get { return foreignIdentificator; }\n        set\n        {\n            if (!GuidPattern.IsMatch(value))\n            {\n                throw new ArgumentException("Invalid Guid format", nameof(value));\n            }\n            foreignIdentificator = value;\n        }\n    }\n\n    public Guid ForeignIdentificatorGuid => new Guid(ForeignIdentificator);\n}