using System;\nusing System.Security.Cryptography.X509Certificates;\n\npublic class CertificateInfo\n{\n    public static void Main()\n    {\n        // Open the certificate store\n        X509Store store = new X509Store("CA", StoreLocation.LocalMachine);\n\n        // Get the certificates\n        X509Certificate2Collection certs = store.Certificates;\n\n        // Iterate through the certificates\n        foreach (X509Certificate2 cert in certs)\n        {\n            // Check if the certificate was issued by this CA\n            if (cert.IssuerName.Name == "Your CA Name")\n            {\n                // Get the certificate details\n                Console.WriteLine("Subject: " + cert.SubjectName.Name);\n                Console.WriteLine("Serial Number: " + cert.SerialNumber);\n                Console.WriteLine("Issued Date: " + cert.NotBefore);\n                Console.WriteLine("Expiration Date: " + cert.NotAfter);\n                Console.WriteLine();\n            }\n        }\n\n        // Close the store\n        store.Close();\n    }\n}