using System;\n\npublic class WifiDistanceCalculator\n{\n    public static double CalculateDistance(double rssi, double frequency)\n    {\n        // Free-space path loss formula\n        double distance = Math.Pow(10, (rssi + 20 * Math.Log10(frequency) - 32.44) / -20);\n        return distance;\n    }\n\n    public static void Main(string[] args)\n    {\n        double rssi = -40; // Received signal strength indication (RSSI) value\n        double frequency = 2412; // Frequency of the WiFi signal (MHz)\n        double distance = CalculateDistance(rssi, frequency);\n        Console.WriteLine("Estimated distance: " + distance + " meters");\n    }\n}