using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\npublic class ConfigurationClient\n{\n    private readonly HttpClient _httpClient;\n    private readonly string _configurationUrl;\n\n    public ConfigurationClient(string configurationUrl)\n    {\n        _configurationUrl = configurationUrl;\n        _httpClient = new HttpClient();\n    }\n\n    public async Task<string> GetConfigurationAsync(string configurationName)\n    {\n        var response = await _httpClient.GetAsync($"{_configurationUrl}/{configurationName}");\n        response.EnsureSuccessStatusCode();\n        return await response.Content.ReadAsStringAsync();\n    }\n}