// ImportingDLL.cs\nusing System;\nusing System.Runtime.InteropServices;\n\nnamespace ImportingDLL\n{\n    [DllImport("ManagedDLL", EntryPoint = "??0UnmanagedClass@QEAA@XZ")]\n    public static extern IntPtr UnmanagedClassConstructor();\n\n    [DllImport("ManagedDLL", EntryPoint = "??1UnmanagedClass@QEAA@XZ")]\n    public static extern void UnmanagedClassDestructor(IntPtr ptr);\n\n    [DllImport("ManagedDLL", EntryPoint = "?DoSomething@UnmanagedClass@QEAAXXZ")]\n    public static extern void UnmanagedClassDoSomething(IntPtr ptr);\n\n    public class ManagedClass\n    {\n        private IntPtr unmanagedClassPtr;\n\n        public ManagedClass()\n        {\n            unmanagedClassPtr = UnmanagedClassConstructor();\n        }\n\n        ~ManagedClass()\n        {\n            UnmanagedClassDestructor(unmanagedClassPtr);\n        }\n\n        public void DoSomething()\n        {\n            UnmanagedClassDoSomething(unmanagedClassPtr);\n        }\n    }\n}