using iTextSharp.text.pdf;\n\n// Open the PDF file\nPdfReader pdfReader = new PdfReader("path/to/form.pdf");\n\n// Get the first page\nPdfPage page = pdfReader.GetPageN(1);\n\n// Get the form fields\nAcroFields formFields = pdfReader.AcroFields;\n\n// Fill out the form fields\nformFields.SetField("FieldName1", "Value1");\nformFields.SetField("FieldName2", "Value2");\n\n// Create a new PDF writer\nPdfWriter pdfWriter = PdfWriter.GetInstance(new FileStream("output.pdf", FileMode.Create));\n\n// Copy the original PDF to the new writer\npdfWriter.AddPage(page);\n\n// Close the writer\npdfWriter.Close();