using System;\nusing System.Data;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a DataSet\n        DataSet dataSet = new DataSet();\n\n        // Create two DataTables\n        DataTable customersTable = new DataTable("Customers");\n        customersTable.Columns.Add("CustomerId", typeof(int));\n        customersTable.Columns.Add("Name", typeof(string));\n\n        DataTable ordersTable = new DataTable("Orders");\n        ordersTable.Columns.Add("OrderId", typeof(int));\n        ordersTable.Columns.Add("CustomerId", typeof(int));\n        ordersTable.Columns.Add("OrderDate", typeof(DateTime));\n\n        // Add data to the tables\n        customersTable.Rows.Add(1, "John Smith");\n        customersTable.Rows.Add(2, "Jane Doe");\n\n        ordersTable.Rows.Add(1, 1, DateTime.Now);\n        ordersTable.Rows.Add(2, 1, DateTime.Now.AddDays(-1));\n        ordersTable.Rows.Add(3, 2, DateTime.Now.AddDays(-2));\n\n        // Add the tables to the DataSet\n        dataSet.Tables.Add(customersTable);\n        dataSet.Tables.Add(ordersTable);\n\n        // Create a DataRelation\n        DataRelation relation = new DataRelation("CustomerOrders",\n            customersTable.Columns["CustomerId"],\n            ordersTable.Columns["CustomerId"]);\n\n        // Add the relation to the DataSet\n        dataSet.Relations.Add(relation);\n\n        // Use LINQ to query the data\n        var query = from customer in customersTable.AsEnumerable()\n                    join order in ordersTable.AsEnumerable()\n                    on customer.Field<int>("CustomerId") equals order.Field<int>("CustomerId")\n                    select new\n                    {\n                        CustomerName = customer.Field<string>("Name"),\n                        OrderDate = order.Field<DateTime>("OrderDate")\n                    };\n\n        // Print the results\n        foreach (var result in query)\n        {\n            Console.WriteLine($"Customer: {result.CustomerName}, Order Date: {result.OrderDate}");\n        }\n    }\n}