using System.Data;\nusing System.Data.SqlServerCe;\n\n// Connection string to your SQL CE database\nstring connectionString = "Data Source=\\path\\to\\your\\database.sdf;Password=your_password;";\n\n// Add a new record\nvoid AddNewRecord(string connectionString, DateTime date, int number)\n{\n    using (SqlCeConnection connection = new SqlCeConnection(connectionString))\n    {\n        connection.Open();\n\n        using (SqlCeCommand command = new SqlCeCommand("INSERT INTO Delivery (Date, Number) VALUES (@date, @number)", connection))\n        {\n            command.Parameters.AddWithValue("@date", date);\n            command.Parameters.AddWithValue("@number", number);\n            command.ExecuteNonQuery();\n        }\n    }\n}\n\n// Update an existing record\nvoid UpdateRecord(string connectionString, int customerID, int orderID, object newValue)\n{\n    using (SqlCeConnection connection = new SqlCeConnection(connectionString))\n    {\n        connection.Open();\n\n        using (SqlCeCommand command = new SqlCeCommand("UPDATE Delivery SET FieldName = @newValue WHERE CustomerID = @customerID AND OrderID = @orderID", connection))\n        {\n            command.Parameters.AddWithValue("@newValue", newValue);\n            command.Parameters.AddWithValue("@customerID", customerID);\n            command.Parameters.AddWithValue("@orderID", orderID);\n            command.ExecuteNonQuery();\n        }\n    }\n}