using iTextSharp.text;\nusing iTextSharp.text.pdf;\n\n// Create a new PDF portfolio\nPdfReader[] pdfReaders = new PdfReader[files.Length];\nfor (int i = 0; i < files.Length; i++)\n{\n    pdfReaders[i] = new PdfReader(files[i]);\n}\n\nPdfWriter writer = PdfWriter.GetInstance(document, Response.OutputStream);\ndocument.Open();\nPdfCollection collection = new PdfCollection(PdfCollection.PDF);\ndocument.AddCollection(collection);\n\n// Add each PDF to the portfolio\nforeach (PdfReader pdfReader in pdfReaders)\n{\n    PdfFileSpecification fs = PdfFileSpecification.FileEmbedded(\n        writer, pdfReader.GetFileSpecification(), null);\n    collection.AddFile(fs);\n}\n\ndocument.Close();\nResponse.ContentType = "application/pdf";\nResponse.AddHeader("Content-Disposition", "attachment; filename=portfolio.pdf");