private double gcX = 0;\nprivate double gcY = 0;\nprivate bool moveUp = false;\nprivate bool moveDown = false;\nprivate bool moveLeft = false;\nprivate bool moveRight = false;\n\nprotected override void OnKeyDown(KeyEventArgs e)\n{\n    base.OnKeyDown(e);\n    Key keyPressed = e.Key;\n\n    if (keyPressed == Key.W)\n    {\n        moveUp = true;\n    }\n    if (keyPressed == Key.S)\n    {\n        moveDown = true;\n    }\n    if (keyPressed == Key.A)\n    {\n        moveLeft = true;\n    }\n    if (keyPressed == Key.D)\n    {\n        moveRight = true;\n    }\n}\n\nprotected override void OnKeyUp(KeyEventArgs e)\n{\n    base.OnKeyUp(e);\n    Key keyPressed = e.Key;\n\n    if (keyPressed == Key.W)\n    {\n        moveUp = false;\n    }\n    if (keyPressed == Key.S)\n    {\n        moveDown = false;\n    }\n    if (keyPressed == Key.A)\n    {\n        moveLeft = false;\n    }\n    if (keyPressed == Key.D)\n    {\n        moveRight = false;\n    }\n}\n\nprivate void MoveCanvas(object sender, EventArgs e)\n{\n    if (moveUp)\n    {\n        gcY += 5;\n    }\n    if (moveDown)\n    {\n        gcY -= 5;\n    }\n    if (moveLeft)\n    {\n        gcX += 5;\n    }\n    if (moveRight)\n    {\n        gcX -= 5;\n    }\n\n    gameCanvas.RenderTransform = new TranslateTransform(gcX, gcY);\n}\n\n// In your constructor or Loaded event handler\npublic MainWindow()\n{\n    InitializeComponent();\n\n    DispatcherTimer timer = new DispatcherTimer();\n    timer.Interval = TimeSpan.FromMilliseconds(16); // 60 FPS\n    timer.Tick += MoveCanvas;\n    timer.Start();\n}