// Search control\npublic event EventHandler<SearchResultsEventArgs> OnSearchResultsUpdated;\n\nprotected void SearchButton_Click(object sender, EventArgs e)\n{\n    // Perform search logic...\n    SearchResults results = GetSearchResults();\n    OnSearchResultsUpdated?.Invoke(this, new SearchResultsEventArgs(results));\n}\n\n// Product control\npublic void Refresh(SearchResults results)\n{\n    // Update product list with search results...\n    UpdatePanel1.Update();\n}\n\n// Page\nprotected void Page_Init(object sender, EventArgs e)\n{\n    SearchControl.OnSearchResultsUpdated += (sender, args) =>\n    {\n        ProductControl.Refresh(args.SearchResults);\n    };\n}