using System;\nusing System.Net.Http;\nusing System.Text;\nusing System.Threading.Tasks;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        // Set the URL of the login page\n        string loginUrl = "https://www.azet.sk/prihlasenie";\n\n        // Set the credentials\n        string username = "your_username";\n        string password = "your_password";\n\n        // Create a new instance of HttpClient\n        using (var client = new HttpClient())\n        {\n            // Set the User-Agent header to mimic a browser\n            client.DefaultRequestHeaders.UserAgent.TryParseAdd("Mozilla/5.0");\n\n            // Create a new FormUrlEncodedContent instance with the login data\n            var loginData = new FormUrlEncodedContent(new[]\n            {\n                new KeyValuePair<string, string>("nick", username),\n                new KeyValuePair<string, string>("password", password)\n            });\n\n            // Post the login data to the login page\n            var response = await client.PostAsync(loginUrl, loginData);\n\n            // Check if the login was successful\n            if (response.IsSuccessStatusCode)\n            {\n                // Get the cookies from the response\n                var cookies = response.Headers.GetValues("Set-Cookie");\n\n                // Use the cookies to authenticate subsequent requests\n                client.DefaultRequestHeaders.Add("Cookie", string.Join("; ", cookies));\n\n                // You are now logged in and can make authenticated requests\n                var authenticatedResponse = await client.GetAsync("https://www.azet.sk/muj-ucet");\n                var responseData = await authenticatedResponse.Content.ReadAsStringAsync();\n\n                Console.WriteLine(responseData);\n            }\n            else\n            {\n                Console.WriteLine("Login failed");\n            }\n        }\n    }\n}