using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class BinaryFileComparer\n{\n    public bool AreFilesEqual(string filePath1, string filePath2)\n    {\n        using (var fileStream1 = File.OpenRead(filePath1))\n        using (var fileStream2 = File.OpenRead(filePath2))\n        {\n            var hash1 = CalculateHash(fileStream1);\n            var hash2 = CalculateHash(fileStream2);\n\n            return hash1.SequenceEqual(hash2);\n        }\n    }\n\n    private byte[] CalculateHash(Stream stream)\n    {\n        using (var sha256 = SHA256.Create())\n        {\n            return sha256.ComputeHash(stream);\n        }\n    }\n}