public class LocalizedException : ApplicationException\n{\n    public string ResourceKey { get; private set; }\n    public object[] Args { get; private set; }\n    public string LocalizedMessage { get; private set; }\n\n    public LocalizedException(string resourceKey, params object[] args)\n        : base()\n    {\n        ResourceKey = resourceKey;\n        Args = args;\n        LocalizedMessage = GetLocalizedMessage(resourceKey, args);\n    }\n\n    private string GetLocalizedMessage(string resourceKey, object[] args)\n    {\n        var resourceManager = new System.Resources.ResourceManager("AppName.ExceptionResources", System.Reflection.Assembly.GetExecutingAssembly);\n        var localText = resourceManager.GetString(resourceKey);\n        try\n        {\n            return string.Format(localText, args);\n        }\n        catch\n        {\n            return localText + string.Join(", ", args.Select(a => a.ToString()));\n        }\n    }\n}