using System;\nusing System.Runtime.InteropServices;\n\nnamespace SmsSendMessage\n{\n    class Program\n    {\n        [DllImport("sms.dll")]\n        static extern int SmsSendMessage(IntPtr hwndParent, string lpszText, string lpszDestinationAddress, uint dwFlags, ref IntPtr lpszMessageID);\n\n        const uint SMS_SEND_MESSAGE_FLAG_REQUEST_STATUS = 0x00000001;\n\n        static void Main(string[] args)\n        {\n            string message = "Hello, World!";\n            string recipientNumber = "+1234567890";\n            IntPtr messageId = IntPtr.Zero;\n\n            int result = SmsSendMessage(IntPtr.Zero, message, recipientNumber, SMS_SEND_MESSAGE_FLAG_REQUEST_STATUS, ref messageId);\n\n            if (result == 0)\n            {\n                Console.WriteLine("Message sent successfully. Message ID: " + messageId);\n            }\n            else\n            {\n                Console.WriteLine("Error sending message. Result: " + result);\n            }\n        }\n    }\n}