using System.Data;\nusing System.Data.SqlServerCe;\n\n// assuming you have a DataTable or DataSet containing the data\nDataTable data = ...;\n\nusing (SqlCeConnection connection = new SqlCeConnection("Data Source=your_database.sdf"))\n{\n    connection.Open();\n\n    using (SqlCeBulkCopy bulkCopy = new SqlCeBulkCopy(connection))\n    {\n        bulkCopy.DestinationTableName = "your_table_name";\n        bulkCopy.WriteToServer(data);\n    }\n}