using System.IO;\nusing System.Security.AccessControl;\n\n// Get the file and folder paths\nstring globalAsaxPath = Path.Combine(Request.PhysicalApplicationPath, "Global.asax");\nstring binFolderPath = Path.Combine(Request.PhysicalApplicationPath, "bin");\n\n// Get the Network Service account\nNTAccount networkServiceAccount = new NTAccount("NT AUTHORITY\\NETWORK SERVICE");\n\n// Grant read and execute permissions on the Global.asax file\nFileInfo globalAsaxFileInfo = new FileInfo(globalAsaxPath);\nFileSecurity globalAsaxFileSecurity = globalAsaxFileInfo.GetAccessControl();\nglobalAsaxFileSecurity.AddAccessRule(new FileSystemAccessRule(networkServiceAccount, FileSystemRights.ReadAndExecute, AccessControlType.Allow));\nglobalAsaxFileInfo.SetAccessControl(globalAsaxFileSecurity);\n\n// Grant read and execute permissions on the /bin folder\nDirectoryInfo binFolderInfo = new DirectoryInfo(binFolderPath);\nDirectorySecurity binFolderSecurity = binFolderInfo.GetAccessControl();\nbinFolderSecurity.AddAccessRule(new FileSystemAccessRule(networkServiceAccount, FileSystemRights.ReadAndExecute, AccessControlType.Allow));\nbinFolderInfo.SetAccessControl(binFolderSecurity);