using Liquibase;\nusing Liquibase.Commands;\n\nclass DatabaseMigration\n{\n    public void Migrate()\n    {\n        // Create a new LiquiBase instance\n        var liquibase = new Liquibase.Liquibase();\n\n        // Set the database connection\n        liquibase.Database = new OracleDatabase("your_connection_string");\n\n        // Set the changelog file\n        liquibase.ChangelogFile = "path/to/your/changelog.xml";\n\n        // Run the migration\n        var migrator = new UpdateDatabaseCommand(liquibase);\n        migrator.Execute();\n    }\n}