using System.Net.Http;\nusing System.Threading.Tasks;\n\npublic class PhpScriptCaller\n{\n    public async Task CallPhpScriptAsync(string url, string parameter1, string parameter2)\n    {\n        using (var client = new HttpClient())\n        {\n            var request = new HttpRequestMessage(HttpMethod.Get, $"{url}?param1={parameter1}&param2={parameter2}");\n            var response = await client.SendAsync(request);\n            response.EnsureSuccessStatusCode();\n            var responseBody = await response.Content.ReadAsStringAsync();\n            // Process the response body\n        }\n    }\n}