// Define a projection for loading only Account ID, name, and settings\npublic class AccountProjection : IProjection\n{\n    public AccountProjection()\n    {\n        AddProperty("Id");\n        AddProperty("Name");\n        AddProperty("Settings");\n    }\n}\n\n// Use the projection to load the data\nvar accounts = session.QueryOver<Account>()\n    .Select(Projections.ProjectionList()\n        .Add(Projections.Property("Id"))\n        .Add(Projections.Property("Name"))\n        .Add(Projections.Property("Settings")))\n    .List<AccountProjection>();