public class HttpModuleLoader : IHttpModule\n{\n    private readonly HttpModuleConfigSection _config;\n\n    public HttpModuleLoader(HttpModuleConfigSection config)\n    {\n        _config = config;\n    }\n\n    public void Init(HttpApplication context)\n    {\n        foreach (HttpModuleElement module in _config.Modules)\n        {\n            Type moduleType = Type.GetType(module.Type);\n            if (moduleType != null)\n            {\n                IHttpModule instance = (IHttpModule)Activator.CreateInstance(moduleType);\n                instance.Init(context);\n            }\n        }\n    }\n\n    public void Dispose()\n    {\n        // Dispose of any resources\n    }\n}