using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Xml;\n\nclass XmlImporter\n{\n    public void ImportXmlData(string xmlFile)\n    {\n        using (SqlConnection conn = new SqlConnection("Your connection string"))\n        {\n            conn.Open();\n\n            using (XmlReader reader = XmlReader.Create(xmlFile))\n            {\n                while (reader.Read())\n                {\n                    if (reader.NodeType == XmlNodeType.Element)\n                    {\n                        // Get the current element's name and value\n                        string elementName = reader.Name;\n                        string elementValue = reader.Value;\n\n                        // Insert the data into the SQL Server database\n                        using (SqlCommand cmd = new SqlCommand("INSERT INTO XmlData (XmlContent) VALUES (@XmlContent)", conn))\n                        {\n                            cmd.Parameters.AddWithValue("@XmlContent", elementValue);\n                            cmd.ExecuteNonQuery();\n                        }\n                    }\n                }\n            }\n        }\n    }\n}