using System;\nusing System.Reflection;\nusing System.ServiceModel;\n\npublic class DynamicProxyFactory\n{\n    public static object CreateProxy(Type contractType, string endpointAddress)\n    {\n        // Create a binding\n        var binding = new BasicHttpBinding();\n\n        // Create an endpoint address\n        var endpoint = new EndpointAddress(endpointAddress);\n\n        // Create a channel factory\n        var channelFactory = new ChannelFactory(contractType, binding, endpoint);\n\n        // Create a proxy\n        var proxy = channelFactory.CreateChannel();\n\n        return proxy;\n    }\n}\n\n// Usage:\nvar contractType = typeof(IMyContract); // Get the contract type from somewhere\nvar endpointAddress = "http://localhost:8080/MyService";\n\nvar proxy = DynamicProxyFactory.CreateProxy(contractType, endpointAddress);\n\n// Use the proxy to call the service\nvar result = ((IMyContract)proxy).MyMethod();