using Amazon.S3;\nusing Amazon.S3.Transfer;\n\npublic void UploadFileToS3(string filePath, string bucketName, string key)\n{\n    using (var transferUtility = new TransferUtility(new AmazonS3Client("accessKeyId", "secretAccessKey", Amazon.Region.USWest2)))\n    {\n        var request = new TransferUtilityUploadRequest\n        {\n            BucketName = bucketName,\n            Key = key,\n            FilePath = filePath\n        };\n\n        request.UploadProgressEvent += (sender, e) =>\n        {\n            Console.WriteLine($"Uploading... {e.TransferredBytes} of {e.TotalBytes}");\n        };\n\n        await transferUtility.UploadAsync(request);\n    }\n}