using System;\nusing System.Collections.Generic;\nusing System.IO;\nusing System.Text;\n\npublic class PoFileGenerator\n{\n    public void GeneratePoFile(string outputFile, Dictionary<string, string> translations)\n    {\n        using (var writer = new StreamWriter(outputFile, false, Encoding.UTF8))\n        {\n            writer.WriteLine("# Generated by my C# program");\n            writer.WriteLine("msgid \"\"");\n            writer.WriteLine("msgstr \"\"");\n\n            foreach (var translation in translations)\n            {\n                writer.WriteLine($"msgid \"{translation.Key}\"");\n                writer.WriteLine($"msgstr \"{translation.Value}\"");\n            }\n        }\n    }\n}