[OperationContract]\n[WebInvoke(Method = "GET", \n    ResponseFormat = WebMessageFormat.Xml, \n    BodyStyle = WebMessageBodyStyle.Wrapped, \n    UriTemplate = "getstreamurl?ch={ch}&format={format}")]\npublic Stream GetStreamUrl(string ch, string format)\n{\n    if (format.ToLower() == "json")\n    {\n        WebOperationContext.Current.OutgoingResponse.ContentType = "application/json";\n        return new MemoryStream(Encoding.UTF8.GetBytes(JsonConvert.SerializeObject(yourData)));\n    }\n    else\n    {\n        return new MemoryStream(Encoding.UTF8.GetBytes(XmlConvert.SerializeObject(yourData)));\n    }\n}