using System.Net.Http;\nusing System.Net.Http.Headers;\n\npublic class TextToSpeech\n{\n    private const string ApiKey = "YOUR_AZURE_SPEECH_SERVICES_API_KEY";\n    private const string ApiRegion = "YOUR_AZURE_SPEECH_SERVICES_REGION";\n\n    public async Task SpeakTextAsync(string text)\n    {\n        var uri = $"https://{ApiRegion}.tts.speech.microsoft.com/cognitiveservices/v1";\n\n        using var client = new HttpClient();\n        client.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Bearer", ApiKey);\n\n        var request = new HttpRequestMessage(HttpMethod.Post, uri)\n        {\n            Content = new StringContent($"{{ \"text\": \"{text}\" }}", Encoding.UTF8, "application/ssml+xml")\n        };\n\n        var response = await client.SendAsync(request);\n\n        if (response.IsSuccessStatusCode)\n        {\n            var audioStream = await response.Content.ReadAsStreamAsync();\n            // Play the audio stream in Silverlight\n        }\n    }\n}