using System;\nusing System.Data.OleDb;\n\nclass ActDatabaseConnector\n{\n    public static void Main(string[] args)\n    {\n        // Specify the ACT! database file and the OLE DB provider\n        string connectionString = "Provider=ACTOLEDB2.1;Data Source=C:\\Path\\To\\Act\\Database.adbf";\n\n        // Create an OLE DB connection object\n        using (OleDbConnection connection = new OleDbConnection(connectionString))\n        {\n            // Open the connection\n            connection.Open();\n\n            // Execute a query to retrieve data from the ACT! database\n            using (OleDbCommand command = new OleDbCommand("SELECT * FROM CONTACT", connection))\n            {\n                using (OleDbDataReader reader = command.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        Console.WriteLine(reader["FIRSTNAME"].ToString());\n                    }\n                }\n            }\n        }\n    }\n}