public string GetArtistThumbnail(string artistName)\n{\n    var request = WebRequest.Create("http://ws.audioscrobbler.com/2.0/?method=artist.getinfo&artist=" + artistName + "&api_key=" + APIKey) as HttpWebRequest;\n\n    using (var response = request.GetResponse() as HttpWebResponse)\n    {\n        using (var reader = XmlReader.Create(response.GetResponseStream()))\n        {\n            var xmlDoc = new XmlDocument();\n            xmlDoc.Load(reader);\n\n            // Manually populate a DataSet or a custom object\n            var artist = xmlDoc.SelectSingleNode("//artist");\n            var name = artist.SelectSingleNode("name").InnerText;\n            var thumbnail = artist.SelectSingleNode("image[@size='small']").InnerText;\n\n            // Return the thumbnail URL\n            return thumbnail;\n        }\n    }\n}