// In the web.config file\n<authentication mode="Forms">\n    <forms cookieless="UseCookies" name=".MyAppAuth" loginUrl="~\Login.aspx" timeout="20" slidingExpiration="true"/>\n</authentication>\n\n// In the code behind\nprotected void Session_Start(object sender, EventArgs e)\n{\n    // Synchronize the session timeout with the forms authentication timeout\n    Session.Timeout = (int)HttpContext.Current.GetOwinContext().Authentication.AuthenticationProperties.Timeout.TotalMinutes;\n}\n\nprotected void Session_End(object sender, EventArgs e)\n{\n    // Sign out when the session ends\n    FormsAuthentication.SignOut();\n}\n\nprotected void FormsAuthentication_OnAuthenticate(object sender, FormsAuthenticationEventArgs e)\n{\n    // Renew the session when the forms authentication ticket is renewed\n    if (e.Ticket != null && e.Ticket.Expiration > DateTime.Now)\n    {\n        Session.Timeout = (int)e.Ticket.Expiration.Subtract(DateTime.Now).TotalMinutes;\n    }\n}