using System;\nusing System.Net;\n\nclass HttpMonitor\n{\n    private HttpListener _listener;\n\n    public HttpMonitor(int port)\n    {\n        _listener = new HttpListener();\n        _listener.Prefixes.Add("http://localhost:" + port + "/");\n    }\n\n    public void Start()\n    {\n        _listener.Start();\n        Console.WriteLine("Listening on port " + _listener.Prefixes[0]);\n    }\n\n    public void Stop()\n    {\n        _listener.Stop();\n    }\n\n    private void _listener_RequestHandler(object sender, HttpListenerRequestEventArgs e)\n    {\n        HttpListenerRequest request = e.Request;\n        HttpListenerResponse response = e.Response;\n\n        // Log the requested URL\n        Console.WriteLine("Request to " + request.Url);\n\n        // Check if the requested URL should be blocked\n        if (ShouldBlock(request.Url))\n        {\n            // Block the request by sending a 403 Forbidden response\n            response.StatusCode = 403;\n            response.StatusDescription = "Forbidden";\n            response.Close();\n        }\n        else\n        {\n            // Allow the request to proceed\n            response.StatusCode = 200;\n            response.StatusDescription = "OK";\n            response.Close();\n        }\n    }\n\n    private bool ShouldBlock(Uri url)\n    {\n        // Implement your blocking logic here\n        // For example, block requests to example.com\n        return url.Host == "example.com";\n    }\n}