using DocumentFormat.OpenXml;\nusing DocumentFormat.OpenXml.Packaging;\nusing DocumentFormat.OpenXml.Spreadsheet;\n\n// Open the Excel file\nusing (SpreadsheetDocument spreadsheetDocument = SpreadsheetDocument.Open("example.xlsx", true))\n{\n    // Get the first sheet\n    Sheet sheet = spreadsheetDocument.WorkbookPart.Workbook.GetFirstChild<Sheets>().GetFirstChild<Sheet>();\n\n    // Get the worksheet part\n    WorksheetPart worksheetPart = (WorksheetPart)spreadsheetDocument.WorkbookPart.GetPartById(sheet.Id);\n\n    // Get the cell at row 1, column 1\n    Cell cell = worksheetPart.Worksheet.GetFirstChild<SheetData>().GetFirstChild<Row>().GetFirstChild<Cell>();\n\n    // Set the cell value\n    cell.CellValue = new CellValue("Hello, World!");\n\n    // Save the changes\n    worksheetPart.Worksheet.Save();\n}