[HttpPost]\npublic ActionResult Create(MyObject obj)\n{\n    // Validate the object\n    if (ModelState.IsValid)\n    {\n        // Instead of saving the object, redirect to the confirmation page\n        return RedirectToAction("ConfirmCreate", new { obj = obj });\n    }\n    return View(obj);\n}\n\npublic ActionResult ConfirmCreate(MyObject obj)\n{\n    // Display the object's details on the confirmation page\n    return View(obj);\n}\n\n[HttpPost]\npublic ActionResult ConfirmCreateConfirmed(MyObject obj)\n{\n    // Recreate the object and save it to the database\n    using (var dbContext = new MyDbContext())\n    {\n        dbContext.MyObjects.Add(obj);\n        dbContext.SaveChanges();\n    }\n    return RedirectToAction("Index");\n}