using System.Drawing;\nusing System.Drawing.Imaging;\nusing System.Collections.Generic;\n\npublic Dictionary<string, string> GetExifProperties(string filePath)\n{\n    Image img = Image.FromFile(filePath);\n    PropertyItem[] propItems = img.PropertyItems;\n\n    Dictionary<string, string> exifProps = new Dictionary<string, string>();\n\n    foreach (PropertyItem propItem in propItems)\n    {\n        switch (propItem.Id)\n        {\n            case 0x9003: // Date Picture Taken\n                exifProps.Add("Date Picture Taken", BitConverter.ToString(propItem.Value));\n                break;\n            case 0x9201: // Shutter Speed\n                exifProps.Add("Shutter Speed", BitConverter.ToString(propItem.Value));\n                break;\n            case 0x927C: // Camera Model\n                exifProps.Add("Camera Model", BitConverter.ToString(propItem.Value));\n                break;\n            // Add more cases for other EXIF properties you're interested in\n        }\n    }\n\n    return exifProps;\n}