// Code-behind\nusing System;\nusing System.Data;\nusing Oracle.DataAccess.Client;\n\npublic partial class MyPage : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Initialize the UpdatePanel\n        UpdatePanel1.UpdateMode = UpdatePanelUpdateMode.Conditional;\n    }\n\n    protected void Button1_Click(object sender, EventArgs e)\n    {\n        // Retrieve the data from the Oracle RefCursor\n        DataTable dt = GetDataFromRefCursor();\n\n        // Bind the data to the GridView\n        GridView1.DataSource = dt;\n        GridView1.DataBind();\n\n        // Update the UpdatePanel\n        UpdatePanel1.Update();\n    }\n\n    private DataTable GetDataFromRefCursor()\n    {\n        // Create an OracleConnection and OracleCommand\n        OracleConnection conn = new OracleConnection("Your Connection String");\n        OracleCommand cmd = new OracleCommand("Your Stored Procedure", conn);\n\n        // Add the RefCursor parameter\n        cmd.Parameters.Add("RefCursor", OracleDbType.RefCursor, ParameterDirection.Output);\n\n        // Execute the stored procedure\n        conn.Open();\n        cmd.ExecuteNonQuery();\n\n        // Retrieve the RefCursor\n        OracleRefCursor refCursor = (OracleRefCursor)cmd.Parameters["RefCursor"].Value;\n\n        // Create a DataTable to store the data\n        DataTable dt = new DataTable();\n\n        // Load the data from the RefCursor into the DataTable\n        dt.Load(refCursor);\n\n        // Close the connection\n        conn.Close();\n\n        return dt;\n    }\n}\n\n// ASPX\n<%@ Page Language="C#" AutoEventWireup="true" CodeFile="MyPage.aspx.cs" Inherits="MyPage" %>\n\n<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\n\n<html xmlns="http://www.w3.org/1999/xhtml">\n<head runat="server">\n    <title>Untitled Page</title>\n</head>\n<body>\n    <form id="form1" runat="server">\n    <asp:ScriptManager ID="ScriptManager1" runat="server" />\n    <asp:UpdatePanel ID="UpdatePanel1" runat="server">\n        <ContentTemplate>\n            <asp:GridView ID="GridView1" runat="server" />\n        </ContentTemplate>\n        <Triggers>\n            <asp:AsyncPostBackTrigger ControlID="Button1" EventName="Click" />\n        </Triggers>\n    </asp:UpdatePanel>\n    <asp:Button ID="Button1" runat="server" Text="Get Data" OnClick="Button1_Click" />\n    </form>\n</body>\n</html>