using System;\nusing System.Security.Cryptography;\nusing System.Security.Cryptography.X509Certificates;\n\npublic class CertificateGenerator\n{\n    public X509Certificate2 GenerateSelfSignedCertificate(string subjectName, string rootCertificateThumbprint)\n    {\n        // Create a new RSA key pair\n        using (var rsa = RSA.Create(2048))\n        {\n            // Create a certificate request\n            var request = new CertificateRequest(subjectName, rsa, HashAlgorithmName.SHA256, RSASignaturePadding.Pkcs1);\n\n            // Create a self-signed certificate\n            var certificate = new X509Certificate2(request.CreateSelfSigned(DateTime.UtcNow, DateTime.UtcNow.AddYears(10)));\n\n            // Add the root certificate to the certificate chain\n            var rootCertificate = new X509Certificate2(rootCertificateThumbprint);\n            certificate.Verify(rootCertificate);\n\n            return certificate;\n        }\n    }\n}