public class BusinessLayer {\n    private int _pageSize = 100; // adjust this value to control the chunk size\n    private int _currentPage = 0;\n    private List<MyObject> _cachedData = new List<MyObject>();\n\n    public List<MyObject> GetData() {\n        if (_cachedData.Count == 0 || _currentPage * _pageSize >= _cachedData.Count) {\n            // load the next chunk of data from the database\n            using (IDataReader reader = ExecuteQuery(_currentPage * _pageSize, _pageSize)) {\n                while (reader.Read()) {\n                    _cachedData.Add(new MyObject(reader));\n                }\n            }\n        }\n\n        // return the current chunk of data\n        return _cachedData.GetRange(_currentPage * _pageSize, _pageSize);\n    }\n\n    public void MoveNextPage() {\n        _currentPage++;\n    }\n\n    public void MovePreviousPage() {\n        _currentPage--;\n    }\n\n    private IDataReader ExecuteQuery(int start, int count) {\n        // implement your database query logic here to retrieve the next chunk of data\n        // use the start and count parameters to control the query\n    }\n}