public void MyMethod(string param1, int param2)\n{\n    if (param1 == null)\n    {\n        throw new ArgumentNullException(nameof(param1), "Parameter cannot be null.");\n    }\n    if (param2 < 0)\n    {\n        throw new ArgumentOutOfRangeException(nameof(param2), "Parameter must be a non-negative integer.");\n    }\n    if (param1.Length == 0)\n    {\n        throw new ArgumentException("Parameter cannot be an empty string.", nameof(param1));\n    }\n    // Method implementation...\n}