using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class ECCExample\n{\n    public static void Main()\n    {\n        // Create a new instance of the ECDsa class\n        using (ECDsa ecdsa = ECDsa.Create(ECCurve.CreateFromFriendlyName("ECDH_P384")))\n        {\n            // Generate a new key pair\n            ecdsa.GenerateKey(ECCurve.CreateFromFriendlyName("ECDH_P384"));\n\n            // Get the public key\n            byte[] publicKey = ecdsa.ExportParameters(false).Q.X;\n\n            // Sign some data\n            byte[] data = Encoding.UTF8.GetBytes("Hello, World!");\n            byte[] signature = ecdsa.SignData(data, HashAlgorithmName.SHA256);\n\n            // Verify the signature\n            bool isValid = ecdsa.VerifyData(data, signature, HashAlgorithmName.SHA256);\n            Console.WriteLine($"Signature is valid: {isValid}");\n        }\n    }\n}