public class IDataErrorInfoBindingBehavior : Behavior<BindingExpression>\n{\n    protected override void OnAttached()\n    {\n        base.OnAttached();\n        AssociatedObject.UpdateSourceExceptionFilter += AssociatedObject_UpdateSourceExceptionFilter;\n    }\n\n    protected override void OnDetaching()\n    {\n        base.OnDetaching();\n        AssociatedObject.UpdateSourceExceptionFilter -= AssociatedObject_UpdateSourceExceptionFilter;\n    }\n\n    private void AssociatedObject_UpdateSourceExceptionFilter(object sender, UpdateSourceExceptionFilterEventArgs e)\n    {\n        var dataErrorInfo = AssociatedObject.DataItem as IDataErrorInfo;\n        if (dataErrorInfo != null)\n        {\n            var error = dataErrorInfo[e.PropertyName];\n            if (error != null)\n            {\n                e.Handled = true;\n                e.Exception = new Exception(error);\n            }\n        }\n    }\n}