public int factorial(int n) {\n    if (n == 0) {\n        return 1;\n    } else {\n        return factorial(n - 1) * n; // NOT tail-recursive\n    }\n}\n\n// Can be rewritten as:\npublic int factorial(int n) {\n    return factorialHelper(n, 1);\n}\n\npublic int factorialHelper(int n, int acc) {\n    if (n == 0) {\n        return acc;\n    } else {\n        return factorialHelper(n - 1, acc * n); // Tail-recursive\n    }\n}