using System.Collections.Generic;\n\npublic class WidgetCollection\n{\n    private Dictionary<string, Widget> widgets = new Dictionary<string, Widget>();\n\n    public void Add(Widget widget)\n    {\n        widgets[widget.FilePath] = widget;\n    }\n\n    public Widget GetByFilePath(string filePath)\n    {\n        return widgets[filePath];\n    }\n\n    public void UpdateFilePath(Widget widget, string newFilePath)\n    {\n        widgets.Remove(widget.FilePath);\n        widget.FilePath = newFilePath;\n        widgets[newFilePath] = widget;\n    }\n}