public IEnumerable<RuleViolation> GetRuleViolations()\n{\n    if (String.IsNullOrEmpty(respName))\n    {\n        yield return new RuleViolation("Responsibility name required", "respName");\n    }\n    if (ChangeAction.Delete == action)\n    {\n        if (respApprovers.Count() != 0 || approvals.Count() != 0)\n        {\n            yield return new RuleViolation("Cannot delete a responsibility that is in use", "resp");\n        }\n    }\n    yield break;\n}