public static MvcHtmlString CustomAjaxActionLink(this HtmlHelper htmlHelper, string text, string actionName, string controllerName, object routeValues, AjaxOptions ajaxOptions)\n{\n    var url = new UrlHelper(htmlHelper.ViewContext.RequestContext);\n    var linkUrl = url.Action(actionName, controllerName, routeValues);\n\n    var link = new TagBuilder("a");\n    link.MergeAttribute("href", linkUrl);\n    link.MergeAttribute("data-ajax", "true");\n\n    if (ajaxOptions != null)\n    {\n        link.MergeAttribute("data-ajax-method", ajaxOptions.HttpMethod);\n        link.MergeAttribute("data-ajax-mode", ajaxOptions.InsertionMode.ToString());\n        link.MergeAttribute("data-ajax-update", ajaxOptions.UpdateTargetId);\n    }\n\n    link.SetInnerText(text);\n\n    var script = new TagBuilder("script");\n    script.SetInnerText("$('a[data-ajax=true]').on('click', function (e) { e.preventDefault(); $.ajax({ type: $(this).data('ajax-method'), url: $(this).attr('href'), success: function (data) { $('#" + ajaxOptions.UpdateTargetId + "').html(data); } }); });");\n\n    return MvcHtmlString.Create(link.ToString() + script.ToString());\n}