using (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n\n    // Set the CONTEXT_INFO value with the user's ID\n    byte[] contextInfo = Encoding.UTF8.GetBytes("UserID:" + userId);\n    connection.Execute("SET CONTEXT_INFO @ContextInfo", new SqlParameter("@ContextInfo", contextInfo));\n\n    // Execute queries or stored procedures as usual\n}